// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./Security.sol";

interface IMyMiniMiners {

    function getPlayerPower(address player) external view returns (uint);

    function getTotalPower() external view returns (uint);

}

contract Rewards is Security, ReentrancyGuard {

    uint private _numberOfBlocksToWaitBetweenClaims = 30 * 60 * 25;

    mapping(address => uint) private _playerLastBlockClaim;

    IMyMiniMiners private _myMiniMinersContract;

    event Claim(address indexed account, uint amount);

    constructor(address adminAccount, address myMiniMinersContract)   ReentrancyGuard() payable {
        setAdminAccount(adminAccount);
        _myMiniMinersContract = IMyMiniMiners(myMiniMinersContract);
    }

    function setNumberOfBlocksToWaitBetweenClaims(uint blocks) external onlyAdmin {
        require(blocks > 30 * 60);
        _numberOfBlocksToWaitBetweenClaims = blocks;
    }

    function claim() external nonReentrant {
        require(address(this).balance > 0);
        require(_myMiniMinersContract.getPlayerPower(_msgSender()) > 0);
        require(_playerLastBlockClaim[_msgSender()] + _numberOfBlocksToWaitBetweenClaims <= block.number);
        uint reward = getPlayerAvailableClaim(_msgSender());
        payable(_msgSender()).transfer(reward);
        _playerLastBlockClaim[_msgSender()] = block.number;
        emit Claim(_msgSender(), reward);
    }

    function getPlayerAvailableClaim(address player) public view returns (uint) {
        if (address(this).balance == 0) {
            return 0;
        }
        uint totalPower = _myMiniMinersContract.getTotalPower();
        if (totalPower == 0) {
            return 0;
        }
        uint playerPower = _myMiniMinersContract.getPlayerPower(player);
        return (playerPower * 1 ether) * (address(this).balance) / (totalPower * 1 ether);

    }
    /*
        getting paid from other contracts
    */
    receive() external payable {

    }

}
